(async () => {
  const imagemin = await import("imagemin");
  const imageminWebp = await import("imagemin-webp");

  await imagemin.default(["build/images/**/*.{jpg,png}"], {
    destination: "build/optimization",
    plugins: [
      imageminWebp.default({
        quality: 75, // Вы можете изменить качество, если это необходимо
      }),
    ],
  });

  console.log("Images converted to webp!");
})();
